mapper.sql:
FROM course_arrange a



    AND a.id = #{id}


    AND a.course_code = #{courseCode}


    AND a.course_name = #{courseName}


    AND a.course_address = #{courseAddress}


    AND a.college_name = #{collegeName}


    AND a.teacher_no = #{teacherNo}


    AND a.class_name = #{className}
